interface StatusBadgeProps {
  status: 'idle' | 'active' | 'success' | 'error';
  label: string;
}

const StatusBadge = ({ status, label }: StatusBadgeProps) => {
  const statusConfig = {
    idle: {
      bg: 'bg-muted',
