@tailwind base;
@tailwind components;
@tailwind utilities;

/*
  Professional Design System
  Modern glassmorphism with refined color palette
*/

@layer base {
  :root {
    --background: 240 15% 6%;
    --foreground: 0 0% 98%;

    --card: 240 15% 9%;
    --card-foreground: 0 0% 98%;

    --popover: 240 15% 9%;
    --popover-foreground: 0 0% 98%;

    --primary: 250 89% 67%;
    --primary-foreground: 0 0% 100%;

    --secondary: 200 95% 55%;
    --secondary-foreground: 0 0% 100%;

    --accent: 160 84% 50%;
    --accent-foreground: 0 0% 100%;

    --muted: 240 12% 16%;
    --muted-foreground: 240 5% 55%;

    --destructive: 0 72% 58%;
    --destructive-foreground: 0 0% 100%;

    --border: 240 12% 18%;
    --input: 240 12% 18%;
    --ring: 250 89% 67%;

    --radius: 1rem;

    /* Enhanced gradients */
    --gradient-primary: linear-gradient(135deg, hsl(250 89% 67%), hsl(200 95% 55%));
    --gradient-hero: linear-gradient(135deg, hsl(250 89% 67% / 0.15), hsl(200 95% 55% / 0.15));
    --gradient-card: linear-gradient(135deg, hsl(240 15% 12%), hsl(240 15% 8%));
    --gradient-glow: radial-gradient(ellipse at center, hsl(250 89% 67% / 0.15), transparent 70%);

    /* Refined shadows */
    --shadow-sm: 0 2px 8px -2px hsl(0 0% 0% / 0.3);
    --shadow-md: 0 8px 24px -4px hsl(0 0% 0% / 0.4);
    --shadow-lg: 0 16px 48px -8px hsl(0 0% 0% / 0.5);
    --shadow-glow: 0 0 60px -12px hsl(250 89% 67% / 0.4);
    --shadow-glow-sm: 0 0 30px -8px hsl(250 89% 67% / 0.3);
  }
}

@layer utilities {
  /* Text gradient */
  .text-gradient {
    @apply bg-clip-text text-transparent;
    background-image: var(--gradient-primary);
  }

  /* Glass card effect */
  .glass-card {
    background: linear-gradient(135deg, hsl(240 15% 12% / 0.8), hsl(240 15% 8% / 0.9));
    backdrop-filter: blur(20px);
    border: 1px solid hsl(240 12% 22%);
    box-shadow: var(--shadow-md);
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .glass-card:hover {
    border-color: hsl(250 89% 67% / 0.3);
    box-shadow: var(--shadow-lg), var(--shadow-glow-sm);
    transform: translateY(-2px);
  }

  /* Premium button */
  .btn-primary {
    background: var(--gradient-primary);
    color: white;
    font-weight: 600;
    padding: 0.875rem 2rem;
    border-radius: 0.75rem;
    box-shadow: 0 4px 20px -4px hsl(250 89% 67% / 0.5);
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .btn-primary:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 30px -4px hsl(250 89% 67% / 0.6);
  }

  .btn-primary:active {
    transform: translateY(0);
  }

  /* Glow orb */
  .glow-orb {
    position: absolute;
    border-radius: 50%;
    filter: blur(80px);
    opacity: 0.4;
    animation: float 8s ease-in-out infinite;
  }

  /* Status indicator */
  .status-dot {
    width: 8px;
    height: 8px;
    border-radius: 50%;
    position: relative;
  }

  .status-dot::after {
    content: '';
    position: absolute;
    inset: -2px;
    border-radius: 50%;
    opacity: 0.4;
    animation: pulse-ring 2s ease-out infinite;
  }

  .status-dot.active::after {
    background: currentColor;
  }
}

/* Keyframes */
@keyframes float {
  0%, 100% {
    transform: translate(0, 0) scale(1);
  }
  33% {
    transform: translate(30px, -30px) scale(1.05);
  }
  66% {
    transform: translate(-20px, 20px) scale(0.95);
  }
}

@keyframes pulse-ring {
  0% {
    transform: scale(1);
    opacity: 0.4;
  }
  100% {
    transform: scale(2);
    opacity: 0;
  }
}

@keyframes shimmer {
  0% {
    background-position: -200% 0;
  }
  100% {
    background-position: 200% 0;
  }
}

/* Scrollbar */
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: hsl(240 15% 8%);
}

::-webkit-scrollbar-thumb {
  background: hsl(240 12% 20%);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: hsl(240 12% 25%);
}

/* Selection */
::selection {
  background: hsl(250 89% 67% / 0.3);
  color: white;
}

