@tailwind base;
@tailwind components;
@tailwind utilities;

/* Professional Design System - Premium Dark Theme */

@layer base {
  :root {
    --background: 230 25% 7%;
    --foreground: 220 20% 97%;

    --card: 230 25% 10%;
    --card-foreground: 220 20% 97%;

    --primary: 250 89% 67%;
    --primary-foreground: 220 20% 97%;
    --primary-glow: 250 89% 75%;

    --secondary: 200 95% 55%;
    --secondary-foreground: 230 25% 7%;

    --muted: 230 20% 18%;
    --muted-foreground: 220 15% 55%;

    --accent: 165 80% 45%;
    --accent-foreground: 220 20% 97%;

    --destructive: 0 72% 55%;
    --destructive-foreground: 220 20% 97%;

    --border: 230 20% 15%;
    --input: 230 20% 15%;
    --ring: 250 89% 67%;

    --radius: 1rem;

    /* Premium Gradients */
    --gradient-primary: linear-gradient(135deg, hsl(250 89% 67%), hsl(200 95% 55%));
    --gradient-subtle: linear-gradient(180deg, hsl(230 25% 12%), hsl(230 25% 8%));
    --gradient-glass: linear-gradient(135deg, hsl(230 25% 15% / 0.6), hsl(230 25% 10% / 0.4));
    --gradient-shine: linear-gradient(135deg, hsl(250 89% 75% / 0.15), transparent 50%, hsl(200 95% 65% / 0.15));
    --gradient-hero: linear-gradient(135deg, hsl(250 89% 67% / 0.12), hsl(200 95% 55% / 0.08), transparent);

    /* Premium Shadows */
    --shadow-sm: 0 2px 8px hsl(230 50% 5% / 0.4);
    --shadow-md: 0 8px 24px hsl(230 50% 5% / 0.5);
    --shadow-lg: 0 16px 48px hsl(230 50% 5% / 0.6);
    --shadow-glow: 0 0 40px hsl(250 89% 67% / 0.25);
    --shadow-glow-lg: 0 0 80px hsl(250 89% 67% / 0.35);
    --shadow-inner: inset 0 1px 0 hsl(220 20% 97% / 0.05);
  }
}

@layer base {
  body {
    @apply bg-background text-foreground antialiased;
    font-feature-settings: "cv02", "cv03", "cv04", "cv11";
  }
}

@layer utilities {
  /* Premium Text Gradient */
  .text-gradient {
    @apply bg-clip-text text-transparent;
    background-image: var(--gradient-primary);
  }

  /* Glass Card Effect */
  .glass-card {
    background: var(--gradient-glass);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    border: 1px solid hsl(var(--border) / 0.5);
    box-shadow: var(--shadow-md), var(--shadow-inner);
  }

  .glass-card:hover {
    border-color: hsl(var(--primary) / 0.3);
    box-shadow: var(--shadow-lg), var(--shadow-glow);
  }

  /* Card Glow Effect */
  .card-glow {
    box-shadow: var(--shadow-md);
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
    border: 1px solid hsl(var(--border));
  }

  .card-glow:hover {
    box-shadow: var(--shadow-lg), var(--shadow-glow);
    border-color: hsl(var(--primary) / 0.4);
    transform: translateY(-4px);
  }

  /* Premium Button Styles */
  .btn-primary {
    background: var(--gradient-primary);
    box-shadow: var(--shadow-md), 0 0 20px hsl(250 89% 67% / 0.3);
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .btn-primary:hover {
    box-shadow: var(--shadow-lg), 0 0 40px hsl(250 89% 67% / 0.5);
    transform: translateY(-2px);
  }

  .btn-primary:active {
    transform: translateY(0);
  }

  /* Glow Orb for Background */
  .glow-orb {
    position: absolute;
    border-radius: 50%;
    filter: blur(80px);
    opacity: 0.5;
    animation: float 8s ease-in-out infinite;
  }

  .glow-orb-primary {
    background: hsl(250 89% 67% / 0.4);
  }

  .glow-orb-secondary {
    background: hsl(200 95% 55% / 0.3);
  }

  /* Animated Border */
  .border-gradient {
    position: relative;
    background: hsl(var(--card));
    border-radius: var(--radius);
  }

  .border-gradient::before {
    content: "";
    position: absolute;
    inset: -1px;
    border-radius: inherit;
    background: var(--gradient-primary);
    opacity: 0;
    transition: opacity 0.3s ease;
    z-index: -1;
  }

  .border-gradient:hover::before {
    opacity: 1;
  }

  /* Shimmer Effect */
  .shimmer {
    position: relative;
    overflow: hidden;
  }

  .shimmer::after {
    content: "";
    position: absolute;
    inset: 0;
    background: var(--gradient-shine);
    transform: translateX(-100%);
    animation: shimmer 3s infinite;
  }

  /* Number Badge */
  .number-badge {
    background: var(--gradient-primary);
    box-shadow: 0 0 30px hsl(250 89% 67% / 0.4);
  }

  /* Status Indicator */
  .status-dot {
    @apply w-2 h-2 rounded-full;
    box-shadow: 0 0 8px currentColor;
  }
}

@layer utilities {
  @keyframes float {
    0%, 100% {
      transform: translateY(0) scale(1);
    }
    50% {
      transform: translateY(-20px) scale(1.05);
    }
  }

  @keyframes shimmer {
    0% {
      transform: translateX(-100%);
    }
    100% {
      transform: translateX(100%);
    }
  }

  @keyframes pulse-ring {
    0% {
      transform: scale(0.9);
      opacity: 0.8;
    }
    50% {
      transform: scale(1);
      opacity: 0.4;
    }
    100% {
      transform: scale(0.9);
      opacity: 0.8;
    }
  }
}

