    --destructive: 0 84% 60%;
    --destructive-foreground: 210 40% 98%;

    --border: 217 33% 20%;
    --input: 217 33% 20%;
    --ring: 195 100% 50%;

    --radius: 0.75rem;

    --gradient-diagnostic: linear-gradient(135deg, hsl(195 100% 50% / 0.1), hsl(217 91% 60% / 0.1));
    --gradient-status: linear-gradient(90deg, hsl(195 100% 50%), hsl(217 91% 60%));
    --shadow-glow: 0 0 30px hsl(195 100% 50% / 0.3);
    --shadow-card: 0 4px 20px hsl(0 0% 0% / 0.4);
