        if (err.name === 'NotAllowedError') {
          setError(t.audio.errors.denied);
        } else if (err.name === 'NotFoundError') {
          setError(t.audio.errors.notFound);
        } else {
          setError(t.audio.errors.unknown);
        }
      }
    }
  };

